mapper.select:
SELECT *
FROM t_log
WHERE t_employee_no = #{t_employee_no}
AND t_log_state != -1
ORDER BY t_log_no DESC
LIMIT 15;

mapper.update:
UPDATE t_log
SET t_log_state = 1
WHERE t_employee_no = #{t_employee_no}
AND t_log_state = 0;

UPDATE t_log
SET t_log_state = -1
WHERE t_employee_no = #{t_employee_no}
AND t_log_state != -1;

mapper.select:
SELECT t_employee_no
FROM t_employee
WHERE t_company_no = #{t_company_no}
AND t_employee_state IN (2,3);

SELECT t_service_name
FROM t_service
WHERE t_service_no = #{t_service_no};
