mapper.update:
UPDATE t_user SET
t_user_photo_path = #{userPhoto},
t_user_name = #{userName}, t_user_email = #{userEmail}, t_user_phone = #{userPhone}, t_user_update_date = now() WHERE t_user_id = #{userId} AND t_user_state != 0;

mapper.select:
SELECT t_user_email
FROM t_user
WHERE t_user_email = #{userEmail} AND t_user_state != 0;

SELECT t_user_phone
FROM t_user
WHERE t_user_phone = #{userPhone} AND t_user_state != 0;

SELECT *
FROM t_notice
WHERE t_company_no = #{companyNo}
AND t_notice_state = 0
ORDER BY t_notice_no DESC
LIMIT 5;
